import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getDocument } from "../actions/DocumentActions";
class Dashboard extends Component {
  constructor() {
    super();
    this.state = {
      documentId: "",
      subject: "",
      topic: "",
      subTopic: "",
      tag: "",
      model: "",
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.handleModelChange = this.handleModelChange.bind(this);
  }
  onChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  onSubmit(event) {
    event.preventDefault();
    const updateDocument = {
      documentId: this.state.documentId,
      subject: this.state.subject,
      topic: this.state.topic,
      subTopic: this.state.subTopic,
      tag: this.state.tag,
      content: this.state.model,
    };
    console.log("This is content");
    console.log(this.state.model);
    this.props.createDocument(updateDocument, this.props.history);
  }
  componentWillReceiveProps(nextProps) {
    const {
      documentId,
      subject,
      topic,
      subTopic,
      content,
      tag,
    } = nextProps.document;
    this.setState({
      documentId: documentId,
      subject: subject,
      topic: topic,
      subTopic: subTopic,
      model: content,
      tag: tag,
    });
  }
  componentDidMount() {
    const { documentId } = this.props.match.params;
    this.props.getDocument(documentId, this.props.history);
  }
  handleModelChange(model) {
    this.setState({
      model: model,
    });
  }
  render() {
    return (
             <div className="documents">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <h1 className="display-4 text-center">Documents</h1>
                            <br />
                   <h1></h1>
                   <input value="this.state.subTopic" />
                            <br />


                        </div>
                    </div>
                </div>
            </div>
    );
  }
}
Dashboard.propTypes = {
  getDocument: PropTypes.func.isRequired,
  document: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  document: state.documents.document
});
export default connect(
  mapStateToProps,
  { getDocument}
)(Dashboard);
