import axios from "axios";
import { GET_ERRORS, GET_DOCUMENTS, GET_DOCUMENT} from "./type";

export const getDocuments = () => async dispatch => {
  const response = await axios.get("http://localhost:9090/api/document/topics");
  dispatch({
    type: GET_DOCUMENTS,
    payload: response.data
  });
};

export const getDocument = (id, history) => async dispatch => {
  const response = await axios.get(`http://localhost:9090/api/document/${id}`);
  dispatch({
    type: GET_DOCUMENT,
    payload: response.data
  });
};
