import React, { Component } from "react";
import { connect } from "react-redux";
import { getDocument } from "../actions/DocumentActions";
import { PropTypes } from "prop-types";
import Content from "./Content"
import axios from "axios";
class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = { document: { topic: '' } };

    this.handleDelete = this.handleDelete.bind(this);
  }

  componentDidMount() {
    const { match:{ params} } = this.props;
    axios.get(`api/content/${params.documentId}`)
    .then(({data:document})=>{this.setState({document});
  });
  }

  render() {
    const {document} =this.state;
    return (
        <div className="container" id="contentboard"> 
          <div className="row">
            <div className="col-md-10">
              <h1>////{document.subTopic}</h1>
            </div>
          </div>
        </div>
    );
  }
}
Dashboard.propTypes = {
  document: PropTypes.object.isRequired,
  getDocument: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  document: state.document
});

export default connect(
  mapStateToProps,
  { getDocument }
)(Dashboard);
