import React, { Component } from "react";
import { Link } from "react-router-dom";

class Content extends Component {
  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getDocument(id, this.props.history);
  }
  render() {
    const { document } = this.props;

    return (
      <div className="container">
        <div className="card card-body bg-light mb-3">
          <div className="row">

            <div className="col-lg-6 col-md-4 col-8">
                {this.state.subject}
              <h3>{document.subject}</h3>
              <h5>{document.topic}</h5>
              <p>{document.subTopic}</p>
            </div>
            <div className="col-md-4 d-none d-lg-block">
              <ul className="list-group">
                <Link to={`/content/${document.documentId}`}>
                  <li className="list-group-item update">
                    <i className="fa fa-edit pr-1"> read more</i>
                  </li>
                </Link>

              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}



Content.propTypes = {
  getDocument: PropTypes.func.isRequired,
  document: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  document: state.projects.document
});
export default connect(
  mapStateToProps,
  { getDocument}
)(Content);
