import React, { Component } from "react";
import { Link } from "react-router-dom";
import Dashboard from "../Dashboard";
import { connect } from "react-redux";
import { getDocuments } from "../../actions/DocumentActions";
import { PropTypes } from "prop-types";
import LeftMenuContent from "./LeftMenuContent";

class LeftMenu extends Component {
  componentDidMount() {
    this.props.getDocuments();
  }
  render() {
    const { documents } = this.props.documents;
    return (
      <div className="wrapper">
        <div id="main-content">
          <div className="row">
            <div
              className="col-3 collapse show d-md-flex bg-light min-vh-100 border border-left-0"
              id="sidebar"
            >
              <ul className="nav flex-column flex-nowrap overflow-hidden">

                <li className="nav-item">jj
                  <a
                    className="nav-link collapsed text-truncate"
                    to="/"
                    data-toggle="collapse"
                    data-target="#submenu1"
                  >
                    <span className="d-none d-sm-inline side-nav-title">

                    {documents.map(document => (
                      <LeftMenuContent key={document.documentId} document={document} />
                    ))}
                    </span>
                  </a>
                  <div class="collapse" id="submenu1" aria-expanded="false">
                    <ul class="flex-column pl-2 nav">
                      <li class="nav-item">
                        <Link class="active nav-link py-0" to="/createDocument">
                          <span class="side-nav-title topics">
                            Create a Document
                          </span>
                        </Link>
                      </li>
                      <li class="nav-item">
                        <a class="nav-link py-0" href="/listDocument">
                          <span class="side-nav-title topics">
                            List Documents
                          </span>
                        </a>
                      </li>
                    </ul>
                  </div>
                </li>
              </ul>
            </div>
            <Dashboard />
          </div>
        </div>
      </div>
    );
  }
}
LeftMenu.propTypes = {
  document: PropTypes.object.isRequired,
  getDocuments: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  documents: state.documents
});

export default connect(
  mapStateToProps,
  { getDocuments }
)(LeftMenu);
