import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getDocument } from "../actions/DocumentActions";

class Dashboard extends Component {
  constructor() {
    super();
    this.state = {
      documentId: "",
      subject: "",
      topic: "",
      subTopic: "",
      content: ""
    };
  }

  componentWillReceiveProps(nextProps) {
    const {
      id,
      projectName,
      projectIdentifier,
      description,
      start_date,
      end_date
    } = nextProps.project;
    this.setState({
      id,
      projectName,
      projectIdentifier,
      description,
      start_date,
      end_date
    });
  }


  componentDidMount() {
    const { documentId } = this.props.match.params;
    this.props.getDocument(documentId, this.props.history);
  }
  render() {
    return (
      <div className="container bg-warning">
        <div className="col-md-12">
        <h1 className="display-4 text-center">Documents</h1>
        <h1>topic:{this.state.topic}</h1>
        <div class="form-group">
                  <input
                    type="text"
                    class="form-control form-control-lg "
                    placeholder="topic Name"
                    name="topic"
                    value={this.state.topic}
                    onChange={this.onChange}
                  />
                </div>
        </div>
      </div>
    );
  }
}
Dashboard.propTypes = {
  getDocument: PropTypes.func.isRequired,
  document: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  document: state.documents.document
});
export default connect(
  mapStateToProps,
  { getDocument}
)(Dashboard);
