import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getDocument } from "../../actions/DocumentActions";

class UpdateDocument extends Component {
  constructor() {
    super();
    this.state = {
      documentId: "",
      subject: "",
      topic: "",
      subTopic: "",
      tag: "",
      model: "",
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.handleModelChange = this.handleModelChange.bind(this);
  }
  onChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  onSubmit(event) {
    event.preventDefault();
    const updateDocument = {
      documentId: this.state.documentId,
      subject: this.state.subject,
      topic: this.state.topic,
      subTopic: this.state.subTopic,
      tag: this.state.tag,
      content: this.state.model,
    };
    console.log("This is content");
    console.log(this.state.model);
    this.props.createDocument(updateDocument, this.props.history);
  }
  componentWillReceiveProps(nextProps) {
    const {
      documentId,
      subject,
      topic,
      subTopic,
      content,
      tag,
    } = nextProps.document;
    this.setState({
      documentId: documentId,
      subject: subject,
      topic: topic,
      subTopic: subTopic,
      model: content,
      tag: tag,
    });
  }
  componentDidMount() {
    const { documentId } = this.props.match.params;
    this.props.getDocument(documentId, this.props.history);
  }
  handleModelChange(model) {
    this.setState({
      model: model,
    });
  }
  render() {
    const document = this.props.document;
    return (
      <div className="card col-md-4 m-auto">
        <div className="card-body">
          <form className="form-group" onSubmit={this.onSubmit}>
            <p className="h4 text-center py-4">Update Document</p>
            {/*Input for Subject */}
            <div className="input-group mb-3">
              {/* <FontAwesomeIcon icon={faAtlas} /> */}
              <div className="input-group-prepend">
                <span className="input-group-text" id="subjectLabel">
                  Subject
                </span>
              </div>
              <input
                type="text"
                name="subject"
                list="subject"
                value={document.subject}
                onChange={this.onChange}
                className="form-control"
                aria-describedby="subjectLabel"
                required
              />
              <datalist id="subject">
                <option value="windows" />
                <option value="OS" />
                <option value="React" />
                <option value="Android" />
              </datalist>
            </div>
            {/*Input for Topic */}
            <div className="input-group mb-3">
              {/* <FontAwesomeIcon icon={faAtlas} /> */}
              <div className="input-group-prepend">
                <span className="input-group-text" id="topicLabel">
                  Topic
                </span>
              </div>
              <input
                type="text"
                name="topic"
                list="topic"
                value={document.topic}
                onChange={this.onChange}
                className="form-control"
                aria-describedby="topicLabel"
                required
              />
              <datalist id="topic">
                <option value="Loops" />
                <option value="Tokens" />
              </datalist>
            </div>
            {/*Input for subTopic */}
            <div className="input-group mb-3">
              {/* <FontAwesomeIcon icon={faAtlas} /> */}
              <div className="input-group-prepend">
                <span className="input-group-text" id="subTopicLabel">
                  Subtopic
                </span>
              </div>
              <input
                type="text"
                name="subTopic"
                value={document.subTopic}
                list="subTopic"
                onChange={this.onChange}
                className="form-control"
                aria-describedby="subTopicLabel"
                required
              />
              <datalist id="subTopic">
                <option value="For" />
                <option value="While" />
                <option value="Do-While" />
                <option value="NO_SUBTOPIC" />
              </datalist>
            </div>
            {/*Input for tag */}
            <div className="input-group mb-3">
              {/* <FontAwesomeIcon icon={faAtlas} /> */}
              <div className="input-group-prepend">
                <span className="input-group-text" id="tagLabel">
                  Tag
                </span>
              </div>
              <input
                type="text"
                name="tag"
                list="tag"
                value={document.tag}
                onChange={this.onChange}
                className="form-control"
                aria-describedby="tagLabel"
                required
              />
              <datalist id="tag">
                <option value="Java" />
                <option value="Html" />
                <option value="SQL" />
                <option value="Oracle" />
              </datalist>
            </div>
            {/*Editor */}
            <div className="form-group">
              <FroalaEditor
                tag="textarea"
                model={this.state.model}
                //data={document.content}
                onModelChange={this.handleModelChange}
              />
            </div>
            {/*Submit Button */}
            <div className="form-group">
              <input
                type="submit"
                name="submit"
                value="Update"
                className="btn btn-primary btn-block mt-4"
              />
            </div>
          </form>
        </div>
      </div>
    );
  }
}
UpdateDocument.propTypes = {
  getDocument: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  document: state.documents.document
});
export default connect(mapStateToProps, { getDocument})(
  UpdateDocument
);
