import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getDocument } from "../actions/DocumentActions";
class Dashboard extends Component {

  componentDidMount() {
    const { documentId } = this.props.match.params;
    this.props.getDocument(documentId, this.props.history);
  }
  render() {
    return (
      <div className="container bg-warning">
        <div className="col-md-12">
        <h1 className="display-4 text-center">Documents</h1>
            <h3>{document.documentId}</h3>
            <h3>{document.subject}</h3>
            {document.map(documentId => (
                   <SideBarContent key={document.documentId} document={document} />
                  ))}
        </div>
      </div>
    );
  }
}
Dashboard.propTypes = {
  getDocument: PropTypes.func.isRequired,
  document: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  document: state.documents.document
});
export default connect(
  mapStateToProps,
  { getDocument}
)(Dashboard);
