import React, { Component } from "react";
import { connect } from "react-redux";
import { getDocument } from "../actions/DocumentActions";
import { PropTypes } from "prop-types";
import Content from "./Content"
import Axios from "axios";
class Dashboard extends Component {
  constructor(props){
    super(props);
    this.state= {document:{topic:''}};
  }
  componentWillReceiveProps(nextProps) {
    const {
      documentId,
      subject,
      topic,
      subTopic,content
    } = nextProps.document;
    this.setState({
      documentId,
      subject,
      topic,
      subTopic,
      content
    });
  }
  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getDocument(id, this.props.history);
  }

  render() {
    const { document } = this.state;
    return (
        <div className="container" id="contentboard"> 
          <div className="row">
            <div className="col-md-10">
              <h1>{document.subTopic}
              <hr></hr>{this.state.subTopic}</h1>
            </div>
          </div>
        </div>
    );
  }
}
Dashboard.propTypes = {
  document: PropTypes.object.isRequired,
  getDocument: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  document: state.document
});

export default connect(
  mapStateToProps,
  { getDocument }
)(Dashboard);
